(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),m=document.getElementById("timer-days"),s=e=>e<10?"0"+e:e;let r=setInterval((()=>{let e=(()=>{let e=(new Date("25 december 2021").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,days:Math.floor(e/60/60/24),hours:Math.floor(e/60/60%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();e.seconds>0&&(m.textContent=e.days,t.textContent=s(e.hours),n.textContent=s(e.minutes),o.textContent=s(e.seconds),e.timeRemaining<0&&clearInterval(r))}),1e3)})()})();